INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Diablo: The Utimate Race',11,null,'2019-01-18',110,2,0,0,1,'Kuba''s sister falls seriously ill. In order to gather the required money for treatment he starts taking part in illegal car races.',31);
