INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Close',11,'Jewson Film','2019-01-18',95,3,0,1,1,'A counter-terrorism expert takes a job protecting a young heiress. After an attempted kidnapping puts both of their lives in danger, they must flee.',14);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('The Kid Who Would Be King',11,'Working Title Films','2019-01-16',120,2,0,0,1,'Old-school magic meets the modern world when young Alex stumbles upon the mythical sword Excalibur. He soon unites his friends and enemies, and they become knights who join forces with the legendary wizard Merlin. Together, they must save mankind from the wicked enchantress Morgana and her army of supernatural warriors.',10);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Harpoon',17,null,'2019-01-24',82,4,0,1,1,'Rivalries, dark secrets, and sexual tension emerge when three best friends find themselves stranded on a yacht in the middle of the ocean under suspicious circumstances.',13);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Sheep & Wolves: Pig Deal',4,'Wizart Animation','2019-01-24',80,2,1,0,1,'The united town of sheep and wolves lives a peaceful and quiet life until two unexpected guests turn up - a polar fox and a tiny ewe. No one ever expected them to bring a deadly danger, which can be overcome only if they work together. Only teamwork can solve big problems and deal with serious challenges - as there is strength in unity.',46);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('The Lego Movie 2: The Second Part',11,'Vertigo Entertainment','2019-01-26',106,3,0,1,1,'It''s been five years since everything was awesome and the citizens are facing a huge new threat: LEGO DUPLO® invaders from outer space, wrecking everything faster than they can rebuild.',53);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Yuppies versus Stooges',5,null,'2019-01-25',109,3,0,0,1,'A group of yuppies fight over a group of office stooges for the control of a shoe factory.',13);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Alita: Battle Angel',11,'Troublemaker Studios','2019-01-31',122,3,0,0,1,'When Alita awakens with no memory of who she is in a future world she does not recognize, she is taken in by Ido, a compassionate doctor who realizes that somewhere in this abandoned cyborg shell is the heart and soul of a young woman with an extraordinary past.',50);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('The Tracker',11,null,'2019-02-01',90,3,0,0,1,'A mysterious stranger travels to a remote village where, 15 years earlier, his wife and daughter were kidnapped.',26);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('The First King',3,'Groenlandia','2019-01-31',123,3,0,0,1,'Romulus and Remus, two shepherds and loyal brothers, end up taking part to a journey that will lead one of them to be the founder of the greatest nation ever seen. However, the fate of the chosen one will pass from killing his own brother.',7);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Benchwarmers 2: Breaking Balls',5,null,'2019-01-29',90,3,0,1,1,'A washed-up baseball player becomes an attorney. He gets a chance at redemption when his boss asks him to manage the company softball team in hopes of winning a championship.',18);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Buddy Games',5,'WWE Studios','2019-02-10',90,4,0,0,1,'A group of friends reunite to play The Buddy Games, a wild assortment of absurd physical and mental challenges. In the process, they''ll heal old wounds, right past wrongs and figure out the true meaning of friendship...or die trying.',31);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Saga of Tanya the Evil: The Movie',4,'NUT','2019-02-08',98,3,0,0,1,'With its armies sweeping across the continent, the Empire seems unstoppable. After securing victory over the remnants of the Republic''s army, the Empire''s ultimate victory is finally within reach. However, dark clouds are gathering in the East. The communist-led Russy Federation is mustering troops on its western border, preparing to enter the war. Supported by a detachment of Allied volunteer magicians—among whom is Mary Sioux, the daughter of a soldier killed by Tanya—the Federation is determined to spread the communist creed and bring the Empire to its knees.INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Cold Pursuit',11,'StudioCanal','2019-02-07',119,3,0,1,1,'The quiet family life of Nels Coxman, a snowplow driver, is upended after his son''s murder. Nels begins a vengeful hunt for Viking, the drug lord he holds responsible for the killing, eliminating Viking''s associates one by one. As Nels draws closer to Viking, his actions bring even more unexpected and violent consequences, as he proves that revenge is all in the execution.',27);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Scooby-Doo! and the Curse of the 13th Ghost',5,'Hanna-Barbera Productions','2019-02-05',82,3,1,0,1,'Mystery Inc. withdraws from solving crimes after botching a case. When Vincent Van Ghoul contacts the gang about an unfinished investigation from Daphne, Shaggy and Scooby''s past, the gang springs into action to finish the job that involves catching the 13th Ghost that escaped from the Chest of Demons and is still at large.',16);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Fall in Love at First Kiss',15,null,'2019-02-14',123,4,0,0,1,'After an earthquake destroys Xiang Qin''s house, she and her father move in with the family of her father''s college buddy. To her surprise, her new kind and amicable aunt and uncle are the parents of her cold and distant schoolmate, Jiang Zhi Shu, a genius with an IQ of 200 whom not too long ago rejected her when she confessed her feelings for him. Will the close proximity give her a second chance to win Zhi Shu''s heart? Or will her love for him end under his cold words?',15);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Is It Wrong to Try to Pick Up Girls in a Dungeon?: Arrow of the Orion',11,'J.C.Staff','2019-02-15',82,4,1,0,1,'In the city of Orario, beneath an impossibly tall tower, lies the dungeon. Only adventurers who form partnerships with the gods themselves have any hope of defeating the monsters that lie within. But the dungeon is not the only place where monsters exist. Far from Orario, in the ruins of an ancient city, a new threat arises. To counter this threat, the goddess Artemis has come to Orario in search of a champion—but it’s not Ais Wallenstein (the legendary Sword Princess) nor Ottar (the strongest warrior to ever enter the dungeon) that she chooses. Rather it is Bell Cranel, a newbie adventurer partnered with a low-tier goddess.',22);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Happy Death Day 2U',5,'Digital Riot Media','2019-02-13',100,3,1,1,1,'Collegian Tree Gelbman wakes up in horror to learn that she''s stuck in a parallel universe. Her boyfriend Carter is now with someone else, and her friends and fellow students seem to be completely different versions of themselves. When Tree discovers that Carter''s roommate has been altering time, she finds herself once again the target of a masked killer. When the psychopath starts to go after her inner circle, Tree soon realizes that she must die over and over again to save everyone.',8);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Innocent Witness',2,'Movierock','2019-02-13',129,3,0,0,1,'An old man suffering from depression is found dead, and his housekeeper, Mi-ran, is charged with the murder. Mi-ran’s defense attorney, Sun-ho, is surprised to learn the only witness to the crime is Ji-woo, a teenage girl with Asperger’s. Will Ji-woo be able to take the stand and provide a valid statement?',30);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Furie',11,'Studio 68','2019-02-22',98,3,1,0,1,'When a little girl is kidnapped by a trafficking ring, they soon find they messed with the wrong child. Her mother, a notorious former gang leader, is close on their trail and will go to any lengths to bring her child home.',22);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Discarnate',8,'Fire Trial Films','2019-02-20',84,1,1,0,1,'A neuroscientist''s obsession with a drug that expands the human mind inadvertently unleashes a deadly supernatural force on his team.',9);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Truth and Justice',2,'Allfilm','2019-02-22',165,1,0,0,1,'Estonia, 1872. The uncompromising new owner of Robber''s Rise must battle with hard work, his spiteful neighbor and with his own family and beliefs to transform the poor land into a flourishing farm of his dreams.',26);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('The Wolf''s Call',17,'Pathé!','2019-02-20',115,3,1,0,1,'With nuclear war looming, a military expert in underwater acoustics strives to prove things aren''t as they seem—or sound—using only his ears.',20);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('4 latas',5,'Wanda Visión','2019-03-01',105,3,0,0,1,'To find Joseba, a dying friend, and see him reunite with his daughter Ely, two old friends, Jean Pierre and Tocho, embark with her on a road trip through the Sahara desert, from Spain to Mali.',27);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('The Hole in the Ground',8,'Bankside Films','2019-03-01',90,3,0,0,1,'Trying to escape her broken past, Sarah O’Neill is building a new life on the fringes of a backwood rural town with her young son Chris. A terrifying encounter with a mysterious neighbour shatters her fragile security, throwing Sarah into a spiralling nightmare of paranoia and mistrust, as she tries to uncover if the disturbing changes in her little boy are connected to an ominous sinkhole buried deep in the forest that borders their home.',51);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Apollo 11',1,'Statement Pictures','2019-03-01',93,4,0,1,1,'A look at the Apollo 11 mission to land on the moon led by commander Neil Armstrong and pilot Buzz Aldrin.',11);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('The Boy Who Harnessed the Wind',2,'Potboiler Productions','2019-03-01',113,4,1,0,1,'Against all the odds, a thirteen year old boy in Malawi invents an unconventional way to save his family and village from famine.',31);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Captain Marvel',11,'Marvel Studios','2019-03-06',124,3,0,0,1,'The story follows Carol Danvers as she becomes one of the universe’s most powerful heroes when Earth is caught in the middle of a galactic war between two alien races. Set in the 1990s, Captain Marvel is an all-new adventure from a previously unseen period in the history of the Marvel Cinematic Universe.',25);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Get Duked!',5,'Highland Midgie','2019-03-08',87,2,0,0,1,'An anarchic, hip-hop inspired comedy that follows four city boys on a wilderness trek as they try to escape a mysterious huntsman.',16);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('70 Big Ones',16,'Sayaka Producciones Audiovisuales','2019-03-08',105,3,0,0,1,'Raquel is desperate: a series of unfortunate events have led to her daughter being taken and she needs a large amount of money to get her back, and fast. After a desperate plea to the bank, she manages to secure the loan she needs. The problem is that precisely at that moment, a peculiar couple of robbers enter the branch: the drug-addicted Jonan and his partner Lola, who is a deadly combination of smart and psychotic. But Raquel’s stakes are too high, and she knows she must think carefully to outsmart them, save herself and get her money.',25);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Baby Jane',2,'Oktober oy','2019-03-08',93,3,1,1,1,'Jane, a small-town girl, has moved to Helsinki and the world is finally open for her. Passionate and excited, she seeks adventure, all the things she has yearned for so long. Jane gravitates to a gay bar at night, staggers in her nervous state of mind, and suddenly there, right next to her, Jane finds her savior: Piki, the most dashing butch in town. Piki is perfect. Jane is hypnotized by Piki''s dark-toned voice. Piki offers her an entire new universe, and even more - dreams.',14);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('No Manches Frida 2',5,'Epoca Films','2019-03-15',102,2,1,0,1,'Ex-con Zequi and company come back for a new adventure.',52);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Five Feet Apart',15,'Wayfarer Entertainment','2019-03-15',116,3,0,1,1,'Seventeen-year-old Stella spends most of her time in the hospital as a cystic fibrosis patient. Her life is full of routines, boundaries and self-control — all of which get put to the test when she meets Will, an impossibly charming teen who has the same illness. There''s an instant flirtation, though restrictions dictate that they must maintain a safe distance between them. As their connection intensifies, so does the temptation to throw the rules out the window and embrace that attraction.',0);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Grisaia: Phantom Trigger The Animation',11,'Bibury Animation Studios','2019-03-15',90,2,0,1,1,'Following the Heath Oslo incident, the existence of the US-Japanese anti-terror organization CIRS has become a matter of public knowledge. CIRS has been rebuilt from the ground up, and its most covert functions spun off to a new agency: SORD (Social Ops, Research & Development).  The goal of SORD is to train a new generation of operatives to defend the country against future threats. To that end, the organization has established a series of schools up and down the country. Mihama Academy, more-or-less left to rot after its abrupt closure, has been given new purpose as one such ''specialist training school''. It now entrusts the misfit girls who attend it with guns and live ammunition.  No matter how much life grinds them down, what future awaits these girls, who''ve themselves chosen the path of the gun?',16);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Volition',7,'Smith Brothers Film Company','2019-03-15',91,2,1,0,1,'A man afflicted with clairvoyance tries to change his fate when a series of events leads to a vision of his own imminent murder.',12);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('The Dirt',2,'10th Street Entertainment','2019-03-22',108,3,0,0,1,'The story of Mötley Crüe and their rise from the Sunset Strip club scene of the early 1980s to superstardom.',3);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Triple Threat',17,'Hamilton Entertainment','2019-03-19',96,3,0,0,1,'A crime syndicate places a hit on a billionaire''s daughter, making her the target of an elite assassin squad. A small band of down-and-out mercenaries protects her, fighting tooth and nail to stop the assassins from reaching their target.',10);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Unplanned',2,null,'2019-03-21',109,2,0,1,1,'As one of the youngest Planned Parenthood clinic directors in the nation, Abby Johnson was involved in upwards of 22,000 abortions and counseled countless women on their reproductive choices. Her passion surrounding a woman''s right to choose led her to become a spokesperson for Planned Parenthood, fighting to enact legislation for the cause she so deeply believed in. Until the day she saw something that changed everything.',5);
INSERT INTO dbo.film (naziv,zanrId,producent,datumIzdavanja,trajanjeUMinutama,dobnoOgranicenjeId,je3D,jeSinhronizovan,jeTitlovan,opis,procenatZarade) VALUES ('Pain and Glory',2,'El Deseo','2019-03-22',114,2,0,0,1,'Salvador Mallo, a filmmaker in the twilight of his career, remembers his life: his mother, his lovers, the actors he worked with. The sixties in a small village in Valencia, the eighties in Madrid, the present, when he feels an immeasurable emptiness, facing his mortality, the incapability of continuing filming, the impossibility of separating creation from his own life. The need of narrating his past can be his salvation.',41);
